div.tabcontents {
    width: 109%;
    left: -21px;
    position: relative;
}
div.tabcontents div#view1 p {
    padding-right: 20px !important;
}
div.tabcontents div#view2 p {
    padding-left: 10px !important;
    padding-right: 25px !important;    
}

div.audio{
    width: -moz-available !important;   
    z-index: 9999 !important; 
    left: 0;
}

audio {
    width: -moz-available !important;
    background-color: black !important;
    opacity: .7 !important;
}

div.audio {
    height: 40px;
    background-color: black;
    opacity: 1 !important;    
}

div.audio_play-pause {
    position: fixed;
	top: 270px;
	right: 10px;
    width: 40px;
    height: 40px;
    cursor: pointer;
    filter: saturate(0%) brightness(75%);
}
div.audio_play-pause.play {
    background: url("https://img.icons8.com/cotton/344/circled-play.png") no-repeat;
    background-size: contain;
}
div.audio_play-pause.pause {
    background: url(https://img.icons8.com/cotton/344/circled-pause.png) no-repeat;
    background-size: contain;
}

span.clickedElementDiziCumle::selection,
span.clickedElementDiziCumle,
span.clickedElementDiziCeviri,
span.clickedElementDiziCevrildi {
    opacity: .85; /* for Windows */
}

div.wrapper {
    margin: 0 100px !important;
}
div.sidebarCommentary {
    width: 600px !important;
    font-size: initial;    
}
div.earAdvWrapper {
    display: none !important;
}

/*https://www.dictionarist.com/english-turkish*/
::selection {
    background-color: #00e3ff !important;
    color: black !important;
}

body {
    zoom: 1.1;
}

body, #logo, #related a {
    font-family: 'Roboto', sans-serif !important;
	AAAfont-family: Georgia, 'Nimbus Roman No9 L', 'Century Schoolbook L', serif;
    AAAfont-family: 'Ubuntu', sans-serif !important;
    AAAfont-family: "Arial Sans Unicode MS", Arial !important;
    background-color: #000 !important;
    text-shadow: 0 0 3px #000 !important;
}
div.center {
    width: auto !important;
}
div#column-left,
div#column-right {
    display: none !important;
}
div#container {
        margin: 0 auto;
    float: unset !important;
}

#related a {
    font-size: 11pt !important;
    font-weight: bold !important;
    background-color: #728e6d !important;
    color: black !important;
    text-shadow: none !important;

}
div#searchbox input {
    background-color: #728e6d !important;
    border: 1px solid black !important;
    border-radius: 5px !important;

}
#QueryForm a#btnLangOpts{
    display: none !important;
}

#QueryForm button {
    font-weight: 300 !important;
}
#breadcrumb li {
    color: lightgray !important;
    text-shadow: none !important;
}
div#content {
    background-color: #333 !important;
    /*rgb(236, 234, 203) !important;*/
    border: 3px solid #5880aa !important;
    border-radius: 10px !important;
}
div#content h2 {
    color: #6bac6a !important;
    padding-top: 10px !important;
}
div#content a#logo {
    background-color: #555 !important;
    border-radius: 5px !important;
}
div#share {
    display: none !important;
}
div.section {
    background-color: #444 !important;
    color: lightgray !important;
    padding: 0px 10px 5px !important;
    width: 97% !important;
    border: 1px solid black !important;

    border-radius: 10px !important;
    font-weight: 500 !important;
    font-size: 12pt !important;
}
div.section h1 {
    color: orange !important;

}
div#cumleler {
    background-color: #444 !important;
}
div#cumleler div.source {
    font-weight: 500 !important;
    font-size: 12pt !important;
}
div#cumleler div.translation {
    AAAfont-family: 'Courgette', sans-serif !important;
    color: #C7BD6C !important;
    AAApadding-bottom: 10px;
    AAAfont-style: italic;
    font-weight: 500 !important;
    font-size: 12pt !important;
    AAAborder-left: 1px solid #3b863a !important;
}
div#cumleler div.speak img:nth-child(n+3) {
    display: none !important;
    width: 70px !important;
}
div.speak {
    width: 70px !important;
}
div#cumleler div.row {
    AAAborder-bottom: 1px solid #3b863a !important;
    width: 100% !important;
    border-radius: 0px !important;
}
div#cumleler div.row:nth-child(odd) {
    background-color: #444 !important;
}
div#cumleler div.row:nth-child(even) {
    background-color: #333 !important;
}
div#cumleler div.row:hover {
    AAAbackground-color: #ffff99 !important;
    outline: 3px solid #cc7000 !important;
}
div#synonyms span {
    color: orange !important;
}
div#related > div:nth-child(2) {
    margin-top: 15px !important;
}

div#adcontainer,
div.ad728x90 {
    display: none !important;
}
ins.adsbygoogle {
    display: none !important;
}
a[rel='nofollow'] {
